import classes from './Dialogs.module.css';
import {NavLink} from 'react-router-dom';

const DialogItem = (props) => {
  let path = "/dialogs/" + props.id;
  return (
    <div className={classes.dialog + ' ' + classes.active}>
      <NavLink to={path}>{props.name}</NavLink>
    </div>
  )
}

const Message = (props) => {
  return (
    <div className={classes.message}>{props.message}</div>
  )
}

const Dialogs = (props) => {
  let dialogs = [
    {id: 1, name: 'Irina'},
    {id: 2, name: 'Victor'},
    {id: 3, name: 'Yuriy'},
    {id: 4, name: 'Olha'},
    {id: 5, name: 'Yliana'},
    {id: 6, name: 'Sulik'}
  ];

  let messages = [
    {id: 1, message: 'hi!'},
    {id: 2, message: 'How are you'},
    {id: 3, message: 'What about your studing?'},
    {id: 4, message: 'Have you already done it?'},
    {id: 5, message: 'Hi there!'},
    {id: 6, message: 'Did you get my message?'}
  ];

  let messagesElements = messages.map(message => <Message message={message.message} />);
  let dialogElements = dialogs.map( dialog => <DialogItem name={dialog.name} id={dialog.id} />);
  
  return (
    <div className={classes.dialogs}>
      <div className={classes.dialogsItems}>
        { dialogElements }
      </div>
      <div className={classes.messages}>
        {  }
      </div>
    </div>
  )
}

export default Dialogs;
