import * as axios from 'axios';
import styles from './Users.module.css';
import userPhoto from '../../images/05.png  ';

let Users = (props) => {

  if (props.users.length === 0) {

    axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response => {
      props.setUsers(response.data.items);
    });

    props.setUsers([
      {id: 1, photoUrl: '../images/01.png', followed: false, fullName: 'Irina', status: 'i am boss', location:  {city: 'Kharkov', country: 'Ukraine'}},
      {id: 2, photoUrl: '../images/ava02.jpg', followed: true, fullName: 'Victor', status: 'i am boss', location:  {city: 'Kharkov', country: 'Ukraine'}},
      {id: 3, photoUrl: '../images/ava03.jpg', followed: false, fullName: 'Yuriy', status: 'i am boss', location:  {city: 'Kharkov', country: 'Ukraine'}},
      {id: 4, photoUrl: '../images/ava04.jpg', followed: true, fullName: 'Olha', status: 'i am boss', location:  {city: 'Kharkov', country: 'Ukraine'}}
    ])
  }


  return <div>
      {
        props.users.map( u => 
          <div key={u.id}>
            <div>
              <img src={ u.photos.small != null ? u.photos : ""} className={styles.userPhoto}/>
            </div>
            <div>
              { u.followed 
              ? <button onClick={() => { props.unfollow(u.id) }}>Unfollow</button> 
              : <button onClick={() => { props.follow(u.id)} }>Follow</button> }
              
            </div>
           <span>
             <span>
              <div>{u.name}</div>
              <div>{u.status}</div>
             </span>
             <span>
              <div>{'u.location.country'}</div>
              <div>{'u.location.city'}</div>
             </span>
           </span>
          </div>)
      }
    </div>
}

export default Users;
