import * as React from 'react';
import * as axios from 'axios';
import styles from './Users.module.css';
import userPhoto from '../../images/05.png';

class Users extends React.Component {
  constructor(props){
    super(props);
  } 

  componentDidMount() {
    axios.get("https://social-network.samuraijs.com/api/1.0/users")
      .then(response => {
       this.props.setUsers(response.data.items);
    });
  }

  render() {
    let pagesCount = this.props.totalUsersCount / this.props.pageSize;
    let pages = [];
    for (let i = 1; i <= pagesCount.length; i++) {
      pages.push[i];
    }
    
    
    return <div>
    <div>
    
      <span className={styles.selectedPage}>1</span>
      <span className={styles.selectedPage}>2</span>
      <span className={styles.selectedPage}>3</span>
      <span className={styles.selectedPage}>4</span>
      <span className={styles.selectedPage}>5</span>
    </div>
    <button onClick={ this.getUsers }>Get users</button>
      {
        this.props.users.map( u => 
          <div key={u.id}>
            <div>
              <img src={ u.photos.small != null ? u.photos : userPhoto } className={styles.userPhoto}/>
            </div>
            <div>
              { u.followed 
              ? <button onClick={() => { this.props.unfollow(u.id) }}>Unfollow</button> 
              : <button onClick={() => { this.props.follow(u.id)} }>Follow</button> }
              
            </div>
           <span>
             <span>
              <div>{u.name}</div>
              <div>{u.status}</div>
             </span>
             <span>
              <div>{'u.location.country'}</div>
              <div>{'u.location.city'}</div>
             </span>
           </span>
          </div>)
      }
    </div>
  }
}


export default Users;
