const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
  users: [
    // {id: 1, followed: false, fullName: 'Irina', status: 'i am boss', location:  {city: 'Kharkov', country: Ukraine}},
    // {id: 2, followed: true, fullName: 'Victor', status: 'i am boss', location:  {city: 'Kharkov', country: Ukraine}},
    // {id: 3, followed: false, fullName: 'Yuriy', status: 'i am boss', location:  {city: 'Kharkov', country: Ukraine}},
    // {id: 4, fullName: 'Olha', status: 'i am boss', location:  {city: 'Kharkov', country: Ukraine}},
    // {id: 5, fullName: 'Yliana', status: 'i am boss', location:  {city: 'Kharkov', country: Ukraine}},
    // {id: 6, fullName: 'Sulik', status: 'i am boss', location:  {city: 'Kharkov', country: Ukraine}},

  ]
};

const usersReducer = (state = initialState, action) => {
  switch(action.type) {
    case FOLLOW:
     return {
        ...state, 
        users: state.users.map(u => {
          
          if (u.id === action.userId) {
            return {...u, followed: true}
          }
          return u;
        })
    }
    case UNFOLLOW:
      return {
        ...state, 
        users: state.users.map(u => {
          
          if (u.id === action.userId) {
            return {...u, followed: false}
          }
          return u;
        })
    }

    case SET_USERS: {
      return {
        ...state,
        users: [ ...state.users, action.users]}
      }

    default:
      return state;
  }
}


export const followAC = (userId) => ({type: FOLLOW, userId });
export const unfollowAC = (userId) => ({type: UNFOLLOW, userId });
export const setUsersAC = (users) => ({type: SET_USERS, users })

export default usersReducer;
